Scanner scan = new Scanner(System.in);

System.out.println("Digite a nota musical (A, B, C, D, E ou F):");
String nota = scan.nextLine().toUpperCase();

double frequencia = 0;

switch (nota) {
    case "A":
        frequencia = 440.0;
        break;
    case "B":
        frequencia = 493.88;
        break;
    case "C":
        frequencia = 523.25;
        break;
    case "D":
        frequencia = 587.33;
        break;
    case "E":
        frequencia = 659.25;
        break;
    case "F":
        frequencia = 698.46;
        break;
}

if (frequencia != 0) {
    System.out.println("A nota musical " + nota + " tem uma frequência de " + frequencia + " Hz.");
} else {
    System.out.println("Nota musical inválida.");
}

Scanner scan = new Scanner(System.in);

System.out.println("Bem-vindo ao Conversor de Unidades de Medida!");
System.out.println("Por favor, selecione a unidade de entrada:");
System.out.println("1. Metro (m)");
System.out.println("2. Centímetro (cm)");
System.out.println("3. Quilômetro (km)");

int escolhaEntrada = scan.nextInt();

System.out.println("Por favor, selecione a unidade de saída:");
System.out.println("1. Metro (m)");
System.out.println("2. Centímetro (cm)");
System.out.println("3. Quilômetro (km)");

int escolhaSaida = scan.nextInt();

System.out.println("Por favor, insira o valor a ser convertido:");
double valor = scan.nextDouble();

double resultado = 0;

if (escolhaEntrada == 1) {
    if (escolhaSaida == 1) {
        resultado = valor;
    } else if (escolhaSaida == 2) {
        resultado = valor * 100;
    } else if (escolhaSaida == 3) {
        resultado = valor / 1000;
    }
} else if (escolhaEntrada == 2) {
    if (escolhaSaida == 1) {
        resultado = valor / 100;
    } else if (escolhaSaida == 2) {
        resultado = valor;
    } else if (escolhaSaida == 3) {
        resultado = valor / 100000;
    }
} else if (escolhaEntrada == 3) {
    if (escolhaSaida == 1) {
        resultado = valor * 1000;
    } else if (escolhaSaida == 2) {
        resultado = valor * 100000;
    } else if (escolhaSaida == 3) {
        resultado = valor;
    }
}

if (resultado <= 3 && resultado >= 1) {
    System.out.println("O valor convertido é: " + resultado);
} else {
    System.out.println("Unidades de medida inválidas. Por favor, tente novamente.");
}

Random r = new Random();
Scanner scan = new Scanner(System.in);

int numeroSecreto = r.nextInt(100) + 1;
int palpite = 0;

System.out.println("Bem-vindo ao jogo de adivinhação!");
System.out.println("Tente adivinhar o número entre 1 e 100.");

while (palpite != numeroSecreto) {
    System.out.print("Digite seu palpite: ");
    palpite = scan.nextInt();

    if (palpite < numeroSecreto) {
        System.out.println("O número secreto é maior. Tente novamente.");
    } else if (palpite > numeroSecreto) {
        System.out.println("O número secreto é menor. Tente novamente.");
    } else {
        System.out.println("Parabéns! Você adivinhou o número!");
    }
}

Scanner scan = new Scanner(System.in);

System.out.print("Digite um número inteiro: ");
int numero = scan.nextInt();

int soma = 0;
int aux = numero;

while (aux != 0) {
    soma += aux % 10;
    aux /= 10;
}

System.out.println("A soma dos dígitos de " + numero + " é " + soma);

Scanner scan = new Scanner(System.in);

System.out.print("Digite um número: ");
int numero = scan.nextInt();

System.out.println("Tabuada de " + numero + ":");

for (int i = 1; i <= 10; i++) {
    int resultado = numero * i;
    System.out.println(numero + " x " + i + " = " + resultado);
}
