Scanner scan = new Scanner(System.in);

System.out.println("Entre com a distância percorrida(em km): " );
int distanciaEmKm = scan.nextInt();

System.out.println("Entre com o tempo gasto(em horas):");
int tempoGastoEmHoras = scan.nextInt();

int distanciaEmMetros = distanciaEmKm * 1000;
int tempoEmSegundos = tempoGastoEmHoras * 3600;

double velocidadeMedia = (double) distanciaEmMetros / tempoEmSegundos;

System.out.printf("A velocidade média é %.2f m/s.", velocidadeMedia);

Scanner scan = new Scanner(System.in);

System.out.println("Entre com o número de horas trabalhadas:");
int horasTrabalhadas = scan.nextInt();

System.out.println("Entre com o valor por hora de trabalho:");
double valorHora = scan.nextDouble();

double salarioBruto = horasTrabalhadas * valorHora;

System.out.printf("O salário bruto deste funcionário é R$ %.2f", salarioBruto);

Scanner scan = new Scanner(System.in);

double valorEmprestimo, taxaJuros, valorTotal, valorParcela;
int qtdMeses;

System.out.println("Entre com o valor do empréstimo:");
valorEmprestimo = scan.nextDouble();

System.out.println("Qual a taxa de juros mensal?");
taxaJuros = scan.nextDouble();

System.out.println("O empréstimo foi em quantos meses?");
qtdMeses = scan.nextInt();

valorTotal = valorEmprestimo + (valorEmprestimo * (taxaJuros / 100) * qtdMeses);
valorParcela = valorTotal / qtdMeses;

System.out.printf("O valor total com juros é R$ %.2f%n", valorTotal);
System.out.printf("O valor de cada parcela será de R$ %.2f%n", valorParcela);

Scanner scan = new Scanner(System.in);
int qtdLitros;
