Scanner scan = new Scanner(System.in);

int numero1, numero2;
char operador;

System.out.println("Digite o primeiro número: ");
numero1 = scan.nextInt();
System.out.println("Digite o segundo número: ");
numero2 = scan.nextInt();
System.out.println("Digite o operador aritmético (+, -, *, /): ");
operador = scan.next().charAt(0);

if (operador == '+') {
System.out.println(numero1 + numero2);
} else if (operador == '-') {
System.out.println(numero1 - numero2);
} else if (operador == '*') {
System.out.println(numero1 * numero2);
} else if (operador == '/') {
System.out.println(numero1 / numero2);
} else {
System.out.println("Operador inválido.");
}

Scanner scan = new Scanner(System.in);

System.out.println("Digite o preço do produto: ");
double preco = scan.nextDouble();
System.out.println("Digite a forma de pagamento (1 - à vista, 2 - no cartão de crédito, 3 - no cartão de débito): ");
int formaPagamento = scan.nextInt();

double valorFinal;

if (formaPagamento == 1 || formaPagamento == 3) {
valorFinal = preco * 0.9;
} else {
valorFinal = preco;
}

System.out.println("Valor final da compra: R$" + valorFinal);

Scanner scan = new Scanner(System.in);

double temperaturaCelsius;
int escolha;

System.out.println("Digite a temperatura em graus Celsius: ");
temperaturaCelsius = scan.nextDouble();

System.out.println("Deseja converter para Fahrenheit (1) ou Kelvin (2)? ");
escolha = scan.nextInt();

if (escolha == 1) {
double temperaturaFahrenheit = (temperaturaCelsius * 9 / 5) + 32;
System.out.println("A temperatura em graus Fahrenheit é: " + temperaturaFahrenheit);
} else {
double temperaturaKelvin = temperaturaCelsius + 273.15;
System.out.println("A temperatura em graus Kelvin é: " + temperaturaKelvin);
}

Scanner scan = new Scanner(System.in);

double salario, aumento, salarioAjustado;

System.out.println("Digite o salário do funcionário: ");
salario = scan.nextDouble();

if (salario <= 1500) {
aumento = salario * 0.15;
} else if (salario <= 2500) {
aumento = salario * 0.10;
} else {
aumento = salario * 0.05;
}

salarioAjustado = salario + aumento;

System.out.println("O salário ajustado é: R$" + salarioAjustado);
